package cn.bingoogolapple.acvp.dustboxview;import android.content.Context;import android.graphics.drawable.ClipDrawable;import android.util.AttributeSet;import android.widget.ImageView;public class DustBoxView extends ImageView {    private static final int MAX_PROGRESS = 10000;    public DustBoxView(Context context, AttributeSet attrs) {        this(context, attrs, 0);    }    public DustBoxView(Context context, AttributeSet attrs, int defStyleAttr) {        super(context, attrs, defStyleAttr);        setBackgroundResource(R.drawable.dustbox_bg);    }    public void setData(int value) {        if (value < 0 || value > 100) {            throw new RuntimeException("value must be between 0 to 100");        } else if (value <= 5) {            setImageResource(R.drawable.clip_dustbox_red);        } else {            setImageResource(R.drawable.clip_dustbox_blue);        }        ClipDrawable clipDrawable = (ClipDrawable) getDrawable();        clipDrawable.setLevel(value * MAX_PROGRESS * 42 / 5700);    }}