package cn.bingoogolapple.acvp.strainerview;import android.content.Context;import android.graphics.drawable.Drawable;import android.graphics.drawable.InsetDrawable;import android.util.AttributeSet;import android.widget.ImageView;public class StrainerView extends ImageView {	public StrainerView(Context context, AttributeSet attrs) {		this(context, attrs, 0);	}	public StrainerView(Context context, AttributeSet attrs, int defStyleAttr) {		super(context, attrs, defStyleAttr);		setBackgroundResource(R.drawable.strainer_bg);	}	public void setData(int value) {		Drawable drawable;		if (value < 0 || value > 100) {			throw new RuntimeException("value must be between 0 to 100");		} else if (value <= 5) {			drawable = getContext().getResources().getDrawable(R.drawable.strainer_red);		} else {			drawable = getContext().getResources().getDrawable(R.drawable.strainer_blue);		}		int insetTop = drawable.getIntrinsicHeight() * (100 - value) / 100;		InsetDrawable insetDrawable = new InsetDrawable(drawable, 0, insetTop, 0, 0);		setImageDrawable(insetDrawable);	}}